import React, { useState } from 'react';
import {
    Breadcrumb, BreadcrumbItem, Card, CardImg, CardBody, CardText,
    Navbar, NavbarBrand, Nav, NavbarToggler, Collapse, NavItem, Jumbotron,
    Button, Modal, ModalBody, ModalHeader, ModalFooter,
    Form, FormGroup, Input, Label, Col, Row
} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Control, LocalForm, Errors } from 'react-redux-form';

const required = val => val && val.length;
const maxLength = len => val => !val || (val.length <= len);
const minLength = len => val => val && (val.length >= len);
const isNumber = val => !isNaN(+val);
const validEmail = val => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val);

function handleSubmit(values) {
    console.log("Current state is: " + JSON.stringify(values));;
    alert("Current state is: " + JSON.stringify(values));
};

function CommentForm() {
    const [modal, setModal] = useState(false);
    const toggle = () => setModal(!modal);

    return (
        <React.Fragment>
            <Button outline onClick={toggle}>
                <i className="fa fa-pencil fa-lg" /> Submit Comment
            </Button>
            <Modal isOpen={modal} toggle={toggle}>
                <ModalHeader toggle={toggle}>Submit Comment</ModalHeader>
                <ModalBody>
                    <LocalForm onSubmit={values => handleSubmit(values)}>
                        <div className="form-group">
                            <div>
                                <Label select>
                                    Rating
                                </Label>
                                <Control.select model=".rating" name="rating" className="form-control">
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </Control.select>
                            </div>
                        </div>
                        <div className="form-group">
                            <div>
                                <Label text>
                                    Your Name
                                </Label>
                                <Control.text model=".author" name="author" className="form-control"
                                placeholder="Your Name"
                                validators={{
                                    required,
                                    minLength: minLength(2),
                                    maxLength: maxLength(15)
                                }} />
                                
                            </div>
                        </div>
                        <div className="form-group">
                            <div>
                                <Label textarea>
                                    Comment
                                </Label>
                                <Control.textarea model=".text" name="text" className="form-control">
                                </Control.textarea>
                            </div>
                        </div>
                        <Button type="submit" color="primary">
                            Submit
                        </Button>
                    </LocalForm>
                </ModalBody>
            </Modal>
        </React.Fragment>
    );
}

function RenderCampsite({ campsite }) {
    return (
        <div className="col-md-5 m-1">
            <Card>
                <CardImg top src={campsite.image} alt={campsite.name} />
                <CardBody>
                    <CardText>{campsite.description}</CardText>
                </CardBody>
            </Card>
        </div>
    );
}

function RenderComments({ comments }) {
    if (comments) {
        return (
            <div className="col-md-5 m-1">
                <h4>Comments</h4>
                {comments.map(comment => <div key={comment.id}>
                    {comment.text}<br />
                    -- {comment.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(new Date(Date.parse(comment.date)))}
                    <p />
                </div>
                )}
                <CommentForm />
            </div>
        );
    }
    return <div />
}

function CampsiteInfo(props) {
    if (props.campsite) {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to="/directory">Directory</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.campsite.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <h2>{props.campsite.name}</h2>
                        <hr />
                    </div>
                </div>
                <div className="row">
                    <RenderCampsite campsite={props.campsite} />
                    <RenderComments comments={props.comments} />
                </div>
            </div>
        );
    }
    return <div />;
}


export default CampsiteInfo;
