import React, { Component } from 'react';

class CampsiteInfo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            campsite: null
        };
    }

    render() {
        if (this.state.campsite) {
            return (
                <div className="row">
                    {this.state.campsite}
                </div>
            );
        }
        else {
            return (
                <div>
                    {this.state.campsite}
                </div>
            );
        }
    }
}

export default CampsiteInfo;
